  let oCount = xCount = 0;
  let tempStrArr = Array.prototype.slice.apply(str);

  tempStrArr.forEach((str) => {
    str = str.toLowerCase();
    if(str === 'o') return oCount++;
    if(str === 'x') return xCount++;
  })

  return oCount === xCount;
}
